import React from "react";
import styled from "styled-components";
import { defaultTheme, typeScale } from "../utils";
import { applyStyleModifiers } from "styled-components-modifiers";
import { FaBeer } from "react-icons/fa";
import { AiOutlineInfo } from "react-icons/ai";

const BUTTON_MODIFIERS = {
  primary: () => `
    color: ${defaultTheme.textColorOnPrimary};
    background-color: ${defaultTheme.primaryColor};
    
      &:hover {
        background-color: ${defaultTheme.PrimaryActiveColor};
        outline: 3px solid ${defaultTheme.PrimaryHoverColor};
      //   outline-offset: 2px;
      }
    `,

  success: () => `
    color: ${defaultTheme.textColorOnPrimary};
    background-color: ${defaultTheme.status.successColor};
    
      &:hover {
        background-color: ${defaultTheme.status.successColorActive};
        outline: 3px solid ${defaultTheme.status.successColor};
      //   outline-offset: 2px;
      }
    `,
  warning: () => `
  color: ${defaultTheme.textColorOnPrimary};
  background-color: ${defaultTheme.status.warningColor};
  
    &:hover {
      background-color: ${defaultTheme.status.warningColorActive};
      outline: 3px solid ${defaultTheme.status.warningColor};
    //   outline-offset: 2px;
    }
  `,
  error: () => `
  color: ${defaultTheme.textColorOnPrimary};
  background-color: ${defaultTheme.status.errorColor};
  
    &:hover {
      background-color: ${defaultTheme.status.errorColorActive};
      outline: 3px solid ${defaultTheme.status.errorColor};
    //   outline-offset: 2px;
    }
  `,
};

const AlertContainer = styled.section`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;
const Alert = styled.div`
  border-radius: 10px;
  align-items: center;
  padding: 1rem;
  margin-bottom: 1rem;
  max-width: 600px;
  box-shadow: rgba(0, 0, 0, 0.35) 0px 5px 15px;
  font-size: 1rem;
  margin: 20px;
  ${applyStyleModifiers(BUTTON_MODIFIERS)}
`;
export const Alerts = () => {
  return (
    <AlertContainer>
      <Alert modifiers={["primary"]}>
        <AiOutlineInfo />
        <strong>alert container</strong>
      </Alert>
      <Alert modifiers={["success"]}>
        <AiOutlineInfo />
        <strong>alert container</strong>
      </Alert>
      <Alert modifiers={["warning"]}>
        <AiOutlineInfo />
        <strong>alert container</strong>
      </Alert>
      <Alert modifiers={["error"]}>
        <AiOutlineInfo />
        <strong>alert container</strong>
      </Alert>
    </AlertContainer>
  );
};
