import React from "react";
import styled from "styled-components";
import { defaultTheme, typeScale } from "../utils";
import { applyStyleModifiers } from "styled-components-modifiers";
import { PrimaryButton } from "./buttons";
import { BsSuitHeartFill } from "react-icons/bs";
import { FaShareAlt, FaEllipsisV } from "react-icons/fa";

const Title = styled.h2`
  font-weight: 300;
`;

const Date = styled.div`
  font-weight: 300;
  margin: 6px 0;
`;

const Description = styled.p`
  font-weight: 300;
`;

const cardContainer = styled.div`
  max-width: 250px;
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  margin: 10px;
  box-shadow: rgba(0, 0, 0, 0.3) 0px 19px 38px,
    rgba(0, 0, 0, 0.22) 0px 15px 12px;
  .main {
    display: flex;
    flex-direction: column;
    flex-wrap: wrap;
    background-color: #264653;
    color: white;
    padding: 10px;
  }
  .about {
    margin: 10px;
  }
  .icons {
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    justify-content: space-around;
    margin: 10px;
  }
`;

export const Card = (props) => (
  <cardContainer>
    {/* <Title>The Benefits of Green Apples</Title>
    <Date>3/2/2019</Date>
    <Description>
      Green apples have a high fiber content which helps in increasing the
      body's metabolism. While consuming an apple, make sure that you're not
      tossing the peel in the trash. Consuming apple with its peel improves the
      overall health. Due to its high fiber content, apple helps in
      detoxification process. It keeps the liver and digestive system away from
      harmful elements.
    </Description>
    <PrimaryButton>0 Comments</PrimaryButton>
    <PrimaryButton>0 Comments</PrimaryButton>
    <PrimaryButton>0 Comments</PrimaryButton> */}
    <figure class="main">
      <img
        src="https://images.unsplash.com/photo-1491553895911-0055eca6402d?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=880&q=80"
        alt="Image of bubbles through a microscope."
      />
      <figcaption>
        <h2>Nike Air Max</h2>
        <p>Soft and Smooth.</p>
      </figcaption>
    </figure>
    <div class="about">
      <p>
        Lorem ipsum dolor sit amet consectetur adipisicing elit. Ab, omnis qui
        eius pariatur dolor expedita esse quasi laboriosam maxime, corporis
        delectus. Voluptates numquam odio provident delectus excepturi accusamus
        debitis cumque.
      </p>
    </div>
  </cardContainer>
);
